module.exports = {
    valueExists: function (valuetoFind, keyToUse, arrayToLookThrough) {
        var result = false;
        
        for (var i = 0; i < arrayToLookThrough.length; i++) {
            if (arrayToLookThrough[i][keyToUse] == valuetoFind) {
                result = true;
            }
        }
        
        return result;
    },
    findRecordByValue: function (valuetoFind, keyToUse, arrayToLookThrough) {
        var result = {};
        
        for (var i = 0; i < arrayToLookThrough.length; i++) {
            if (arrayToLookThrough[i][keyToUse] == valuetoFind) {
                result = arrayToLookThrough[i];
            }
        }
        
        return result;
    },
    shuffleArray : function(array) {
        for (var i = array.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = array[i];
            array[i] = array[j];
            array[j] = temp;
        }
        return array;
    }
};